import React, { useEffect, useState } from "react"
import { DataTable } from "react-native-paper"

import { ScrollView, View, Text, StyleSheet } from "react-native"
import AsyncStorage from "@react-native-async-storage/async-storage"
import AddEditTask from "./component/AddEditTask"

const optionsPerPage = [2, 3, 4]

const TaskScreen = ({ navigation }) => {
  const [page, setPage] = React.useState(0)
  const [itemsPerPage, setItemsPerPage] = React.useState(optionsPerPage[0])

  React.useEffect(() => {
    setPage(0)
  }, [itemsPerPage])

  return (
    <View>
      <AddEditTask />
      <DataTable>
        <DataTable.Header>
          <DataTable.Title>TÃ¢che</DataTable.Title>
          <DataTable.Title numeric>Date Depart</DataTable.Title>
          <DataTable.Title numeric>Date Finale</DataTable.Title>
        </DataTable.Header>

        <DataTable.Row>
          <DataTable.Cell>Frozen yogurt</DataTable.Cell>
          <DataTable.Cell numeric>159</DataTable.Cell>
          <DataTable.Cell>   <Button icon="delete" onPress={() => setModalVisible(true)} /> </DataTable.Cell>
        </DataTable.Row>

        <DataTable.Row>
          <DataTable.Cell>Ice cream sandwich</DataTable.Cell>
          <DataTable.Cell numeric>237</DataTable.Cell>
          <DataTable.Cell numeric>8.0</DataTable.Cell>
        </DataTable.Row>

        <DataTable.Pagination
          page={page}
          numberOfPages={3}
          onPageChange={(page) => setPage(page)}
          label="1-2 of 6"
          optionsPerPage={optionsPerPage}
          itemsPerPage={itemsPerPage}
          setItemsPerPage={setItemsPerPage}
          showFastPagination
          optionsLabel={"Rows per page"}
        />
      </DataTable>
    </View>
  )
}

export default TaskScreen

