import React, { useEffect, useState } from "react"
import { Button, DataTable } from "react-native-paper"

import { ScrollView, View, Text, StyleSheet } from "react-native"
import AsyncStorage from "@react-native-async-storage/async-storage"
import AddEditTask from "./component/AddEditTask"

const optionsPerPage = [2, 3, 4]

const data = [
  { title: "Tâche 1", start: "01/01/2020", end: "01/01/2020" },
  { title: "Tâche 2", start: "01/00/2022", end: "01/2/2020" },
  { title: "Tâche 3", start: "06/05/2020", end: "01/11/2020" },
]

const TaskScreen = ({ navigation }) => {
  const [page, setPage] = React.useState(0)
  const [itemsPerPage, setItemsPerPage] = React.useState(optionsPerPage[0])

  React.useEffect(() => {
    setPage(0)
  }, [itemsPerPage])

  {
  }

  return (
    <View>
      <AddEditTask />
      <DataTable>
        <DataTable.Header>
          <DataTable.Title>Tâche</DataTable.Title>
          <DataTable.Title numeric>Date Départ</DataTable.Title>
          <DataTable.Title numeric>Date Finale</DataTable.Title>
          <DataTable.Title numeric>Action</DataTable.Title>
        </DataTable.Header>

        {data.map((task, key) => tableRow(task, key))}

        <DataTable.Pagination
          page={page}
          numberOfPages={3}
          onPageChange={(page) => setPage(page)}
          label="1-2 of 6"
          optionsPerPage={optionsPerPage}
          itemsPerPage={itemsPerPage}
          setItemsPerPage={setItemsPerPage}
          showFastPagination
          optionsLabel={"Rows per page"}
        />
      </DataTable>
    </View>
  )

  function tableRow(task , key) {
    return (
      <DataTable.Row key={key} >
        <DataTable.Cell>{task.title}  </DataTable.Cell>
        <DataTable.Cell numeric> {task.start} </DataTable.Cell>
        <DataTable.Cell numeric> {task.end} </DataTable.Cell>
        <DataTable.Cell  style={{textAlign: 'center',}} >
          <Button  style={{marginLeft : 15}} icon="delete" />{" "}
        </DataTable.Cell>
      </DataTable.Row>
    )
  }
}

export default TaskScreen

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    // height: 500,
    // paddingBottom: 50,
    // width: "100%",
    // maxWidth: 340,
    // alignSelf: "center",
    // alignItems: "center",
    // justifyContent: "center",
  },
})
