      />
      <TextInput
        style={styles.input}
        label="Email"
        value={text}
        onChangeText={(text) => setText(text)}
      />
    </View>
  )
}

function textInput({ keyboardType, label, value }) {
  return (
    <TextInput
      style={{ height: 40 }}
      keyboardType={keyboardType}
      label={label}
      value={formData[value]}
      returnKeyType="next"
      onChangeText={(text) => {
        setFormData({ ...formData, [value]: text })
      }}
    />
  )
}

const styles = StyleSheet.create({
  input: {
    marginBottom: 10,
  },
})

export default Form
