  //   drinker: userData.drinker || false,
  //   sportive: userData.sportive || false,
  //   cholesterol: userData.cholesterol || false,
  //   glucose: userData.glucose || false,
  // })

  const submit = async () => {
    const currentUser = JSON.parse(await AsyncStorage.getItem("user"))

    Axios.patch("/user/sendInformations", { ...formData, id: currentUser._id })
      .then((res) => {
        setFeedback(res.data)

        setTimeout(() => {
          navigation.reset({
            index: 0,
            routes: [{ name: "Profile" }],
          })
        }, 1000)
      })
      .catch((err) => {
        setFeedback(err.response.data)
      })
  }

  return (
    <View>
      <TextInput
        label="Email"
        value={text}
        onChangeText={(text) => setText(text)}
        style={styles.input}
      />
      <TextInput
        style={styles.input}
        label="Email"
        value={text}
        onChangeText={(text) => setText(text)}
      />
      <TextInput
        style={styles.input}
        label="Email"
        value={text}
        onChangeText={(text) => setText(text)}
      />
    </View>
  )
}

function textInput({ keyboardType, label, value }) {
  return (
    <TextInput
      style={{ height: 40 }}
      keyboardType={keyboardType}
      label={label}
      value={formData[value]}
      returnKeyType="next"
      onChangeText={(text) => {
        setFormData({ ...formData, [value]: text })
      }}
    />
  )
}

const styles = StyleSheet.create({
  input: {
    marginBottom: 10,
  },
})

export default Form
